import React, { Component } from 'react'
import * as d3 from 'd3'

export default class LineCHart extends Component {

  componentDidMount() {
    this.DrawChart()
  }

  DrawChart = () => {
    const margin = {top: 20, right: 30, bottom: 80, left: 50}
    const width = this.props.width - margin.left - margin.right
    const height = this.props.height - margin.top - margin.bottom
    let parseTime = d3.timeParse('%d.%m.%Y')
    let data = this.props.data.data
    let buffer = []

    data.forEach((d,i) => {
      buffer.push(Object.values(d))
      // format the date string into date type
      buffer[i][0] = parseTime(buffer[i][0])
      if(typeof buffer[i][1] != 'number'){
        // number string into int
        buffer[i][1] = parseInt(buffer[i][1])
      }
    })

    let x = d3.scaleTime()
      .domain(d3.extent(buffer, d => d[0]))
      .range([0, width])


    let y = d3.scaleLinear()
      .domain([0,d3.max(buffer, d => d[1])])
      .range([height, 0])


    // manipulate svg and create group
    let svg = d3.select('.svgBarChart')
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom)
      .append('g')
      .attr('transform',
            `translate(${margin.left},${margin.top})`)

    // add the x axis
    svg.append('g')
      .attr('transform', `translate(0,${height})`)
      .call(d3.axisBottom(x).tickFormat(d3.timeFormat('%d.%m.%Y')))
      .selectAll('text')
      .attr("transform", "rotate(-90)")
      .style("text-anchor", "end")
      .attr("dx", "-.8em")
      .attr("dy", "-.55em")


   // add the y axis
   svg.append('g')
     .call(d3.axisLeft(y))
  
    // svg.append("g")
    //   .attr("class", "x axis")
    //   .attr("transform", "translate(0," + height + ")")
    //   .call(xAxis)
    //   .selectAll("text")
    //   .style("text-anchor", "end")
    //   .attr("dx", "-.8em")
    //   .attr("dy", "-.55em")
    //   .attr("transform", "rotate(-90)" );
  
    // svg.append("g")
    //   .attr("class", "y axis")
    //   .call(yAxis)
    //   .append("text")
    //   .attr("transform", "rotate(-90)")
    //   .attr("y", 6)
    //   .attr("dy", ".71em")
    //   .style("text-anchor", "end")
    //   .text("Value ($)");
  
    svg.selectAll("rect")
      .data(data)
      .enter().append("rect")
      .style("fill", "steelblue")
      .attr("x", d => x(d[0]))
      .attr("width", 10)
      .attr("y", d => y(d[1]) )
      .attr("height", d => height - y(d[1]))
  }

  render() {
    return <svg className="svgBarChart"></svg>
  }
}
